/* Rutinas Varias de JavaScript */

function acoplarserie(ser, lon) {

    var salida, l;

    if (ser.length >= lon) {
        salida = ser.substring(0, lon);
    } else {
        l = ser.length;
        salida = ser;
        for (var i = l + 1; i <= lon; i++) {
            salida = salida + ' ';
        }
    }

    return salida;

}

function acoplarseriemax(ser, lon) {

    var salida;

    if (ser.length >= lon) {
        salida = ser.substring(0, lon);
    } else {
        salida = ser;
    }

    return salida;

}

function ajax_submit(form, temporal) {

    $(form).submit(function (event) {

        event.preventDefault();    //prevent default action

        var btn = $(':focus');
        if (btn.prop('id') != 'submit') {
            return;
        }

        var nuevos = "";
        var primero = true;
        var valo = "";
        var aa;

        var etiq = $(form).find('*');

        etiq.each(function (index) {

            if (primero == true) {
                valo = "";
            } else {
                valo = "&";
            }

            if ($(this).prop('tagName') == "INPUT") {
                aa = $(this);
                if (aa.attr("name") != undefined && aa.attr("type") != 'checkbox') {
                    nuevos += valo + aa.attr('name') + '=' + aa.val().split(" ").join("%20");
                    primero = false;
                } else if (aa.attr("name") != undefined && aa.attr("type") == 'checkbox') {
                    if (aa.is(":checked")) {
                        nuevos += valo + aa.attr('name') + '=' + aa.val();
                        primero = false;
                    }
                } else if (aa.attr("name") != undefined && aa.attr("type") == 'radio') {
                    if (aa.is(":checked")) {
                        nuevos += valo + aa.attr('name') + '=' + aa.val();
                        primero = false;
                    }
                }
            }

            if ($(this).prop('tagName') == "SELECT") {
                aa = $(this);
                if (aa.attr("name") != undefined) {
                    nuevos += valo + aa.attr('name') + '=' + aa.val();
                    primero = false;
                }
            }

            if ($(this).prop('tagName') == "TREU-TEXTO") {
                aa = $(this);
                if (aa.attr("name") != undefined) {
                    nuevos += valo + aa.attr('name') + '=' + aa.val().split(" ").join("%20");
                    primero = false;
                }
            }
            if ($(this).prop('tagName') == "TREU-NUMER") {
                aa = $(this);
                if (aa.attr("name") != undefined) {
                    nuevos += valo + aa.attr('name') + '=' + aa.val();
                    primero = false;
                }
            }
            if ($(this).prop('tagName') == "TREU-COMBODLG") {
                aa = $(this);
                if (aa.attr("name") != undefined) {
                    nuevos += valo + aa.attr('name') + '=' + aa.val().split(" ").join("%20");
                    primero = false;
                }
            }
            if ($(this).prop('tagName') == "TREU-COMBODLGNUM") {
                aa = $(this);
                if (aa.attr("name") != undefined) {
                    nuevos += valo + aa.attr('name') + '=' + aa.val();
                    primero = false;
                }
            }
            if ($(this).prop('tagName') == "TREU-FECHA") {
                aa = $(this);
                if (aa.attr("name") != undefined) {
                    nuevos += valo + aa.attr('name') + '=' + aa.val();
                    primero = false;
                }
            }
            if ($(this).prop('tagName') == "TREU-TEXTAREA") {
                aa = $(this);
                if (aa.attr("name") != undefined) {
                    nuevos += valo + aa.attr('name') + '=' + aa.val().split(" ").join("%20");
                    primero = false;
                }
            }
            if ($(this).prop('tagName') == "TREU-PASSWORD") {
                aa = $(this);
                if (aa.attr("name") != undefined) {
                    nuevos += valo + aa.attr('name') + '=' + aa.val().split(" ").join("%20");
                    primero = false;
                }
            }
        });

        event.preventDefault(); //prevent default action
        var post_url = $(this).attr("action"); //get form action url
        var request_method = $(this).attr("method"); //get form GET/POST method
        //var form_data = $(this).serialize()+nuevos; //Encode form elements for submission
        var form_data = nuevos; //Encode form elements for submission

        $.ajax({
            url: post_url,
            type: request_method,
            async: false,
            data: form_data
        }).done(function (response) { //
            leer_errores(temporal);
            var ele = $("#mensajes_error").find('.erri');
            if (ele.length > 0) {
                $('#mensajes_error').modal({ backdrop: 'static', keyboard: false });
                $("#mensajes_error").modal('show');
            } else {
                location.href = './' + temporal + '/blanca.php';
            }
        });

    });
}

function leervalor_str(stri) {

    var str, lon, i, str2, numero, car;

    str = stri;
    str2 = '';

    lon = str.length;
    for (i = 1; i <= lon; i++) {
        car = str.substr(i - 1, 1);
        if (car !== ' ' && car !== ',' && car !== '.') {
            str2 += car;
            continue;
        }
        if (car === ',') {
            str2 += '.';
        }
    }

    if (str2 !== '') {
        numero = parseFloat(str2);
    }

    return numero;
}

function leer_errores(temporal) {

    getFileFromServer("./" + temporal + "/errores.txt", function (text) {
        $('.erri').remove();
        if (text === null) {
        } else {
            var lines = text.split('\n');
            if (lines.length - 1 > 0) {
                for (var line = 0; line < lines.length - 1; line++) {
                    $('.errores').append('<li class="erri list-group-item" >' + lines[line] + '</li>');
                }
            }
        }
    });

}

function getFileFromServer(url, doneCallback) {

    var xhr;

    xhr = new XMLHttpRequest();
    xhr.onreadystatechange = handleStateChange;
    xhr.open("GET", url, false);
    xhr.send();

    function handleStateChange() {
        if (xhr.readyState === 4) {
            doneCallback(xhr.status == 200 ? xhr.responseText : null);
        }
    }

}

function letraacentuada(letra) {

    if (letra == 'a') return 'á';
    if (letra == 'e') return 'é';
    if (letra == 'i') return 'í';
    if (letra == 'o') return 'ó';
    if (letra == 'u') return 'ú';

    if (letra == 'A') return 'Á';
    if (letra == 'E') return 'É';
    if (letra == 'I') return 'Í';
    if (letra == 'O') return 'Ó';
    if (letra == 'U') return 'Ú';

    return letra;

}

function leer_lista_campos(vector_texto, campo) {

    var str = vector_texto;
    if (str.indexOf("#[#" + campo + "#]#") >= 0) {
        var pos = str.indexOf("#[#" + campo + "#]#");
        var pos_ini = str.indexOf('#[#', pos + ("#[#" + campo + "#]#").length);
        pos_ini += 3;
        var pos_fin = str.indexOf('#]#', pos_ini);
        return str.substring(pos_ini, pos_fin);
    }
    return "";
}

function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}

function paso_espacios_web(texto) {
    return texto.replace(/ /g, '%20');
}

function paso_espacios_html(texto) {
    return texto.replace(/ /g, "&nbsp;");
}

function getFileExtension(filename) {
    var ext = /^.+\.([^.]+)$/.exec(filename);
    return ext == null ? "" : ext[1];
}

function createCookie(name, value, days) {
    var expires;
    if (days) {
        var date = new Date();
        date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
        expires = "; expires=" + date.toGMTString();
    }
    else {
        expires = "";
    }
    document.cookie = escape(name) + "=" + escape(value) + expires + "; path=/";
}

function eliminarCookie(key) {
    document.cookie = key + '; expires=Thu, 01 Jan 1970 00:00:01 GMT;';
}

function toTimestamp(strDate) {  // strDate formato = YYYY-MM-DD
    var anno = strDate.substring(0, 4);
    var mes = strDate.substring(5, 7);
    var day = strDate.substring(8, 10);
    str = day + '/' + mes + '/' + anno;
    var datum = Date.parse(strDate);
    return datum / 1000;
}

function parseBoolean(str) {
    return /^true$/i.test(str);
}

module.exports = {
    parseBoolean, toTimestamp, eliminarCookie, createCookie, getFileExtension,
    paso_espacios_html, paso_espacios_web, beep, leer_lista_campos, letraacentuada, getFileFromServer, leer_errores,
    leervalor_str, ajax_submit, acoplarserie, acoplarseriemax
}